import React, { useState, useEffect } from 'react'
import { Link, useNavigate, useParams } from 'react-router-dom'
import { Form, Button, Container, FormControl, Row, Col } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import axios from 'axios'
import Message from '../components/Message'
import Loader from '../components/Loader'
import { listProductDetails, updateProduct } from '../actions/productAction'
import { PRODUCT_UPDATE_RESET } from '../constants/productConstants'



const ProductEditScreen = () => {
  const params = useParams()
  const productId = params.id;
  const navigate = useNavigate();

  const [name, setName] = useState('')
  const [price, setPrice] = useState(0)
  const [image, setImage] = useState('')
  const [brand, setBrand] = useState('')
  const [category, setCategory] = useState('')
  const [countInStock, setCountInStock] = useState(0)
  const [description, setDescription] = useState('')

  // const [rating, setRating] = useState('')
  // const [numReviews, setNumReviews] = useState('')
  // const [uploading, setUploading] = useState(false)
  // const [images, setImages] = useState([])
  // const [showCropper, setShowCropper] = useState(false)
  // const [cropImage, setCropImage] = useState(false)
  // const [imageOne, setImageOne] = useState(null)

  const dispatch = useDispatch()

  const productDetails = useSelector((state) => state.productDetails)
  const { loading, error, product } = productDetails

  const productUpdate = useSelector((state) => state.productUpdate)
  const { loading: loadingUpdate, error: errorUpdate, success: successUpdate } = productUpdate





  useEffect(() => {

    if (successUpdate) {
      dispatch({ type: PRODUCT_UPDATE_RESET })
      navigate('/admin/productlist')
    } else {
      if (!product.name || product._id !== productId) {
        dispatch(listProductDetails(productId))

      } else {
        setName(product.name)
        setPrice(product.price)
        setImage(product.image)
        setBrand(product.brand)
        setCategory(product.category)
        setCountInStock(product.countInStock)
        setDescription(product.description)
        setRating(product.rating)
        setNumReviews(product.numReviews)
      }
    }
  }, [dispatch, navigate, productId, product, successUpdate])

  const submitHandler = (e) => {
    e.preventDefault()
    //  dispatch(updateProduct({
    //    _id: productId,
    //    name,
    //    price,
    //    image,
    //    brand,
    //    category,
    //    description,
    //    countInStock,
    //  }))
    const formData = new FormData()
    formData.set('name', name)
    formData.set('price', price)
    // formData.set('discountPrice', discountPrice)
    formData.set('countInStock', countInStock)
    formData.set('image', image)
    formData.set('brand', brand)
    formData.set('rating', rating)
    formData.set('category', category)
    // formData.set('subCategory', subCategory)
    formData.set('description', description)
    formData.set('numReviews', numReviews)
    images.forEach((image) => {
      formData.append('images', image)
    })
    dispatch(updateProduct(formData, productId))
  }

  const uploadFileHandler = async (image) => {
    // const file = e.target.files[0]
    const formData = new FormData()
    formData.append('image', image,image.originalname)
    setUploading(true)

    try {
      const config = {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      }

      const { data } = await axios.post('/api/upload', formData, config)

      setImage(data)
      setUploading(false)
    } catch (error) {
      setUploading(false)
    }
  }

  const multiFileHandler = async (e) => {
    const files = Array.from(e.target.files)
    setImages([])
    files.forEach((file) => {
      const reader = new FileReader()

      reader.onload = () => {
        if (reader.readyState === 2) {
          setImages((oldArray) => [...oldArray, reader.result])
        }
      }
      reader.readAsDataURL(file)
    })
  }

  return (
    <>
      <Container>
        <Link to='/admin/productlist' className='btn btn-light my-3'>
          Go Back
        </Link>
        <Container>
          <Row>
            <Col md={6} className='m-auto'>
              <h1>Edit Product</h1>
              {loadingUpdate && <Loader />}
              {errorUpdate && <Message variant='danger'>{errorUpdate}</Message>}
              {loading ? (
                <Loader />
              ) : error ? (
                <Message variant='danger'>{error}</Message>
              ) : (
                <Form onSubmit={submitHandler}>
                  <Form.Group controlId='name'>
                    <Form.Label>Name</Form.Label>
                    <Form.Control
                      type='name'
                      placeholder='Enter name'
                      value={name}
                      onChange={(e) => setName(e.target.value)}
                    ></Form.Control>
                  </Form.Group>

                  <Form.Group controlId='price'>
                    <Form.Label>price</Form.Label>
                    <Form.Control
                      type='number'
                      placeholder='Enter price'
                      value={price}
                      onChange={(e) => setPrice(e.target.value)}
                    ></Form.Control>
                  </Form.Group>

                  <Form.Group controlId='image'>
                    <Form.Label>Image</Form.Label>
                    <Form.Control
                      type='text'
                      placeholder='Enter image URL'
                      value={image}
                      // onChange={(e) => setImage(e.target.value)}
                    ></Form.Control>
                    <FormControl
                      type="file"
                      name="imageOne"
                    onChange={(e) => {
                      setCropImage(e.target.files[0])
                      setShowCropper(true)
                    }}
                    accept=".jpg,.jpeg,.png,"
                    />
                    {/* {uploading && <Loader />} */}
                  </Form.Group>
{/* 
                  <Form.Group className="py-1">
                    <Form.Label>Add extra images</Form.Label>
                    <FormControl
                      type="file"
                      id="image-files"
                      label="Choose Files"
                      custom
                      multiple
                      onChange={multiFileHandler}
                    />
                    {uploading && <Loader />}
                  </Form.Group> */}

                  <Form.Group controlId='brand'>
                    <Form.Label>Brand</Form.Label>
                    <Form.Control
                      type='text'
                      placeholder='Enter brand '
                      value={brand}
                      onChange={(e) => setBrand(e.target.value)}
                    ></Form.Control>
                  </Form.Group>

                  <Form.Group controlId='countInStock'>
                    <Form.Label>Count In Stock</Form.Label>
                    <Form.Control
                      type='number'
                      placeholder='Enter countInStock'
                      value={countInStock}
                      onChange={(e) => setCountInStock(e.target.value)}
                    ></Form.Control>
                  </Form.Group>



                  <Form.Group controlId='description'>
                    <Form.Label>Description</Form.Label>
                    <Form.Control
                      type='text'
                      placeholder='Enter description '
                      value={description}
                      onChange={(e) => setDescription(e.target.value)}
                    ></Form.Control>
                  </Form.Group>

                  <Button type='submit' variant='primary'>
                    Update
                  </Button>

                </Form>
              )}

            </Col>
          </Row>
        </Container>

      </Container>
    </>
  )
}

export default ProductEditScreen
