import React, {useState,useEffect}from 'react'
import {Link, link} from 'react-router-dom'
import {Form,Button,Row,Col} from 'react-bootstrap'
import { useNavigate,useSearchParams } from 'react-router-dom'
import{useDispatch,useSelector} from 'react-redux'
import Message from '../components/Message'
import Loader from '../components/Loader'
import {login} from '../actions/userActions'
import FormContainer from '../components/FormContainer'

console.log("login page got");

const LogginScreen = () => {
const [email,setEmail]=useState('')
const [password,setPassword]=useState('')
const dispach = useDispatch()
const useerLogin = useSelector(state=>state.userLogin)
const {loading,error,userInfo} =useerLogin
const [searchParams,setSearchParams] =useSearchParams();
const redirect = searchParams.get('redirect')||''
console.log(redirect);
const navigate =useNavigate()

useEffect(()=>{
    if(userInfo){
      navigate(`/${redirect}`)
    }
  },[userInfo,redirect])

const submitHndler = (e)=>{
    e.preventDefault()
    //DISPATCH LOGIN
}

  return <FormContainer>
      <h1>Sign In</h1>
      <Form onSubmit={submitHndler}>
          <Form.Group controlId='email'>
          <Form.Label type='email' placeholder='Enter email' >Email Address</Form.Label>
<Form.Control type ='email' placeholder='Enteremail' value={email} onChange={(e)=>setEmail(e.target.value)}>

</Form.Control>
          </Form.Group>
          <Form.Group controlId='password'>
          <Form.Label type='email' placeholder='Enter Password' >Password</Form.Label>
<Form.Control type ='email' placeholder='Enteremail' value={password} onChange={(e)=>setPassword(e.target.value)}>

</Form.Control>
          </Form.Group>
          <Button type='submit' varient='primery'>
              Sign in
          </Button>
      </Form>
      <Row className='py-3'>
          <Col>
          <Button onClick={() => navigate('/signup')} type='button' size='sm' className='rounded'>Create new</Button>
          </Col>
      </Row>
      </FormContainer>

}

export default LogginScreen
