import {
    USER_LOGIN_FAIL,
    USER_LOGIN_REQUEST,
    USER_LOGIN_SUCCESS,
    USER_LOGOUT
} from "../constants/userConstants";

export const userLoginReducer = (state = {}, actin) => {
    switch (action.type) {
        case USER_LOGIN_REQUEST:
            return { loading: true, }
        case USER_LOGIN_SUCCESS:
            return { loading: false, userInfo: actoin.payload }
        case USER_LOGIN_FAIL:
            return { loading: false, error: actoin.payload }
            case USER_LOGOUT:
                return{}

        default:
            return state
    }
}
