import { CART_ADD_ITEM,
    CART_REMOVE_ITEM,
CART_SAVE_SHIPPING_ADDRESS,
CART_SAVE_PAYMENT_METHOD } from "../constants/cartConstands";

export const cartReducer = (state = { cartItems: [],shipiingAddress:{} }, action) => {
    switch (action.type) {
        case CART_ADD_ITEM:
            const item = action.payload

            const existItem = state.cartItems.find(x => x.product === item
                .product)


            if (existItem) {
                return {
                    ...state,
